import React, { Component } from "react";
import "./Login.css";
import API from "../../utils/API";
import { Modal, Button, Row, Input, Card } from "react-materialize";
import { Link } from "react-router-dom";
import randtoken from "rand-token";
import NavBar2 from "../NavBar2";




class Login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            name: "",
            email: "",
            username: "",
            password: "",
            userId: ""
        }
    }

    handleInputChange = event => {
        let value = event.target.value;
        let name = event.target.name

        this.setState({
            [name]: value
        });
    };

    handleSubmitSignUp = (e) => {
        e.preventDefault();
        const { name, email, username, password } = this.state;
        let formData = new FormData();

        formData.append('name', name)
        formData.append('email', email);
        formData.append('username', username);
        formData.append('password', password);

        let token = randtoken.generate(16);

        let userData = {
            name: this.state.name,
            email: this.state.email,
            username: this.state.username,
            password: this.state.password,
            token: token
        }

        //console.log("userdata", userData)

        API.saveUser({
            name: userData.name,
            email: userData.email,
            username: userData.username,
            password: userData.password,
            token: userData.token
        }).then((result) => {
            //console.log("result: ", result)
            window.localStorage.setItem("token", result.data.token)
            window.localStorage.setItem("name", result.data.name)
            window.location = "/create"
        })
    }

    handleSubmitLogin = (e) => {
        e.preventDefault();
        const { username, password } = this.state;
        let loginData = new FormData();

        loginData.append('username', username);
        loginData.append('password', password);

        //console.log("logindata", loginData)

        

        API.getUser(this.state.username)
        .then((result) => {
            //console.log(result.data)

            if (this.state.password === result.data.password) {
                let token = randtoken.generate(16);
                window.localStorage.setItem("token", token)
                window.localStorage.setItem("name", result.data.name)

                let userInfo = {
                    username: this.state.username,
                    token: token
                }

                API.updateUserToken({userInfo})
                
                

                window.location = "/create"



            }
            else {
                alert("Nom d'utulisateur ou mot de passe Invalid")
            }

        })

        


        // API.saveUser({
        //     name: loginData.name,
        //     email: loginData.email,
        //     username: loginData.username,
        //     password: loginData.password,
        // }).then((result) => {
        //     console.log("result: ", result)
        // })

        console.log("username:", this.state.username)
    }

    render() {

        return (
            <div id="body">
                <NavBar2 />
                <Card className='container' id="logincontainer">
                    <Row>
                        <h5 id='appName'>e-Passport</h5>
                    </Row>
                    <Row>
                        <Input onChange={this.handleInputChange} s={12} name="username" type="text" label="Nom d'utilisateur"  />
                    </Row>
                    <Row>
                        <Input onChange={this.handleInputChange} s={12} name="password" type="password" label="mot de passe"  />
                    </Row>
                    <Row>
                        <Button onClick={this.handleSubmitLogin}>s'identifier</Button>
                    </Row>
                    <Modal
                        header='Sign Up'
                        trigger={<Button>S'inscrire</Button>}>
                        <Row>
                            <Input onChange={this.handleInputChange} s={12} name="name" label="Nom" />
                            <Input onChange={this.handleInputChange} name="email" type="email" label="Email" s={12} />
                            <Input onChange={this.handleInputChange} s={12} name="username" label="Nom d'utilisateur" />
                            <Input onChange={this.handleInputChange} name="password" type="password" label="mot de passe" s={12} />           
                            <Button onClick={this.handleSubmitSignUp} className="modal-action modal-close"><Link to={"/create"}>Cr√©e</Link></Button>
                        </Row>
                    </Modal>
                </Card>
            </div>

        );
    }
}

export default Login;
